1900_unique_sites.map:
function(doc) {
  if(doc.owner_id && doc['csp-report']) {
  var base_uri = doc['csp-report']['document-uri'].split('/').splice(0,3).join('/');
  emit([doc.owner_id, base_uri], 1);
 }
}

1910_stale.map:
function(doc) {
  if(doc.meta && doc.meta.timestamp) {
    var doc_date = new Date(doc.meta.timestamp.split('T')[0]);
    var week_ago = new Date(Date.now()-259200000);
    if(doc_date < week_ago) {
        emit(doc.meta.timestamp, null);
    }
    }
  }

1000_known_list.map:
function (doc) {
    if (doc['owner_id'] && doc['review_type'] && doc['review_source'] && doc['review_action']) {
        emit(doc['owner_id'], [doc['review_type'], doc['review_source'], doc['review_action']])
    }
}

1100_source_groups.map:
function (doc) {
    if (doc['owner_id'] && doc['csp-report'] && doc['csp-report']['blocked-uri']) {
        if(!doc['review'] || (doc['review'] && doc['review']['decision'] === 'unknown')) {
	        blocked_uri = doc['csp-report']['blocked-uri'];
        if (/^data:/.test(blocked_uri)) {
            // truncate the BASE64 encoded data from URI
            blocked_uri = blocked_uri.split(',')[0];
        }
        if (/^https?/.test(blocked_uri)) {
            // truncate URI to exclude params after ? or #
            blocked_uri = blocked_uri.match(/^(https?:\/\/[^?#]+)/)[1];
            blocked_uri = blocked_uri.split('/').slice(0,3).join('/');
        }

        emit([ doc['owner_id'], doc['csp-report']['violated-directive'].split(' ')[0], blocked_uri, ], 1);

    } }
}
